type:
∀ {E : Type u_2} [inst : NormedAddCommGroup E] [inst_1 : NormedSpace ℝ E] [inst_2 : CompleteSpace E] {β : Type u_3}
  [inst_3 : TopologicalSpace β] [inst_4 : MeasurableSpace β] [inst_5 : BorelSpace β] {μ : MeasureTheory.Measure β}
  {f : β → E},
  MeasureTheory.Integrable f →
    (∀ (s : Set β), IsClosed s → ∫ (x : β) in s, f x ∂μ = 0) → f =ᶠ[MeasureTheory.Measure.ae μ] 0

∀ {α : Type u_1} {F : Type u_3} [inst : NormedAddCommGroup F] [inst_1 : NormedSpace ℝ F] {m : MeasurableSpace α}
  {μ : MeasureTheory.Measure α} [inst_2 : DecidablePred fun x => x ≠ 0] {f : MeasureTheory.SimpleFunc α F}
  {s : Finset F},
  Finset.filter (fun x => x ≠ 0) (MeasureTheory.SimpleFunc.range f) ⊆ s →
    MeasureTheory.SimpleFunc.integral μ f = Finset.sum s fun x => ENNReal.toReal (↑↑μ (↑f ⁻¹' {x})) • x
